#if __ANDROID__
using System.Collections.Generic;using Android.Graphics;
using EOS.UI.Droid.Themes;
using EOS.UI.Shared.Helpers;using EOS.UI.Shared.Themes.Extensions;using EOS.UI.Shared.Themes.Helpers;using static EOS.UI.Shared.Sandbox.Helpers.Constants;namespace EOS.UI.Shared.Sandbox.ControlConstants.Android{    public static class FabProgressConstants    {
        private static ShadowConfig ShadowConfiguration => (ShadowConfig) EOSThemeProvider.Instance.GetCurrentTheme().ThemeValues[EOSConstants.FabShadow];

        public static Dictionary<string, Color> BackgroundColors =>            Colors.MainColorsCollection.AddDefault(Fields.Default, (Color)EOSThemeProvider.Instance.GetCurrentTheme().ThemeValues[EOSConstants.BrandPrimaryColor]);        public static Dictionary<string, Color> PressedBackgroundColors =>            Colors.MainColorsCollection.AddDefault(Fields.Default, (Color)EOSThemeProvider.Instance.GetCurrentTheme().ThemeValues[EOSConstants.BrandPrimaryColorVariant1]);        public static Dictionary<string, Color> DisabledBackgroundColors =>            Colors.MainColorsCollection.AddDefault(Fields.Default, (Color)EOSThemeProvider.Instance.GetCurrentTheme().ThemeValues[EOSConstants.NeutralColor4S]);        public static Dictionary<string, Color> ShadowColors =>        Colors.MainColorsCollection.AddDefault(Fields.Default, ShadowConfiguration.Color);        public static Dictionary<string, int> ShadowOffsetXCollection =>            Shadow.OffsetCollection.AddDefault(Fields.Default, ShadowConfiguration.Offset.X);        public static Dictionary<string, int> ShadowOffsetYCollection =>            Shadow.OffsetCollection.AddDefault(Fields.Default, ShadowConfiguration.Offset.Y);        public static Dictionary<string, int> ShadowRadiusCollection =>            Shadow.RadiusCollection.AddDefault(Fields.Default, ShadowConfiguration.Blur);        public static Dictionary<string, double> ShadowOpacityCollection        {            get            {                return Shadow.OpacityCollection.AddDefault(Fields.Default, ShadowConfiguration.Color.A);            }        }    }}
#endif